package com.spring.root.controller;

import java.io.IOException;
import java.io.PrintWriter;
import java.io.UnsupportedEncodingException;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Controller;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;

@Controller
public class MailController {
	@Autowired
	MailService service;
	
	@GetMapping("/sendmail")
	public void sendSimpleMail(HttpServletRequest request, HttpServletResponse response) throws IOException {
		request.setCharacterEncoding("utf-8");
		response.setContentType("text/html;charset=utf-8");
		PrintWriter out = response.getWriter();
		service.sendMail("wooyh0115@naver.com", "테스트 메일(제목)","안녕하세요");
		out.print("메일을 보냈습니다.");
	}
	
	@GetMapping("/sendmail2")
	public void semdSimpleMail2(HttpServletRequest request, HttpServletResponse response) throws IOException {
		request.setCharacterEncoding("utf-8");
		response.setContentType("text/html;charset=utf-8");
		PrintWriter out = response.getWriter();
		
		StringBuffer sb = new StringBuffer();
		sb.append("<html><body>");
		sb.append("<h1>제품소개<h1>");
		sb.append("<a href='https://tv.naver.com/'>");
		sb.append("<img src='data:image/png;base64,'");
		sb.append("</a></body></html>");
		String str = sb.toString();
		
		service.sendMail("wooyh0115@naver.com", "테스트 메일(광고~)",str);
		out.print("메일을 보냈습니다.");
	}
	
	@RequestMapping("auth")
	public String auth(HttpServletRequest request) {
		service.auth(request);
		return "redirect:http://www.naver.com";
	}
	
	@RequestMapping("auth_check")
	public String auth_check(@RequestParam("userid") String userid, @RequestParam("userkey") String userkey, HttpSession session) {
		String sessionkey = (String)session.getAttribute(userid);
		if(sessionkey != null) {
			if(sessionkey.equals(userkey)) {
				session.setAttribute("userid", userid);
			}
		}
		return "redirect:authform";
	}
	
	@RequestMapping("authform")
	public String authform() {
		return "authform";
	}
}
